mapper.update:
update sys_user
set name=#{name},
role=#{role},
status=#{status}
where id = #{id}

update sys_user
set pwd=#{new_pwd}
where id = #{id}
and pwd = #{old_pwd}

update sys_user
set avatar=#{avatar}
where id = #{id}

mapper.select:
select a.*,b.name role_name
from sys_user a
left join sys_role b on a.role = b.id
where a.name = #{name}
and a.pwd = #{pwd}

select if(count(0) > 0, 1, 0)
from sys_user
where name = #{name}
and id!=#{id}

select if(count(0) > 0, 1, 0)
from sys_user
where id = #{id}
and pwd = #{pwd}
